import React, { Component } from 'react';
import Header from "../../../common/Header";

class SpecialOffer extends Component {

  render() {
    return (
      <div className={'offer-wrap'}>
        <div>Thank you for your purchase!</div>
        <div className={'action'}>SPECIAL OFFER</div>
        <div>As a thank you for shopping with us,
          get this additional item for <b>only $15!</b>
        </div>
        <div>HURRY, YOUR OFFER ENDS IN:</div>
        <div className={'time'}>00:</div>
      </div>
    )
  }
}

export default SpecialOffer;
