import React, { Component } from "react";
import less from '../../assets/images/less.svg'
import more from '../../assets/images/more.svg'

class PlusMinusButtons extends Component {
  state = {
    min: this.props.min || 0,
    max: this.props.max || 10,
    value: this.props.value || 0
  };

  onChange = (e) => {
    const { onChange, name } = this.props;

    let result =  e.target.value;
    if (result.length > 1 && result.charAt(0) === '0') result=result.substring(1);

    this.setState({ value: result }, () => onChange(name, result));
  };

  onPlus = () => {
    const { onChange, dinamyc, name } = this.props;

    let result = this.state.value + 1;
    result = result > this.state.max ? this.state.max : result;

    let fieldName = dinamyc ? 'plus' : name;
    this.setState({ value: result }, () => onChange(fieldName, result));
  };

  onMinus = () => {
    const { onChange, dinamyc, name } = this.props;

    let result = this.state.value - 1;
    result = result < this.state.min ? this.state.min : result;

    let fieldName = dinamyc ? 'minus' : name;
    this.setState({ value: result }, () => onChange(fieldName, result));
  };

  componentDidUpdate(prevProps) {
    const { value } = this.props;

    if (prevProps.value !== value) {
      this.setState({ value });
    }
  }

  render() {
    const { value, max, min } = this.state;

    return (
      <div className={'button-wrap'} >
        <div onClick={this.onMinus}>
          <img src={less} alt=""/>
        </div>
        <input
          className="btn_value"
          type="number"
          name="quantity"
          min={min}
          max={max}
          value={value}
          onChange={this.onChange}
        />
        <div allow={`${value === max}`} onClick={this.onPlus}>
          <img src={more} alt=""/>
        </div>
      </div>
    );
  }
}

