import React from 'react';
import PurchasePage from './components/pages/PurchasePage';
import './App.scss';
import ConformationPage from "./components/pages/ConformationPage";

class App extends Component {

  state = {
    displayPurchasePage: false,
    displayConformationPage: false,
  };


  render () {
    const { displayPurchasePage, displayConformationPage } =this.state;
    return (
      <div className="App">
        <button onClick={() =>this.setState({ displayPurchasePage: true })}>
          Display Purchase Page
        </button>
        <button onClick={() =>this.setState({ displayConformationPage: true })}>
          Display Conformation Page
        </button>
        {displayPurchasePage && <PurchasePage />}
        {displayConformationPage && <ConformationPage />}
      </div>
    );
  }
}

export default App;
